{% set hu = get_handling_unit(doc.name) %}
{% set label = zebra_zpl_label(width=4*203, length=6*203, dpi=203) -%}

{{ label.add(zebra_zpl_barcode(hu.handling_unit, width=4, height=260, position=(20, 40), justification="C", ratio=1, human_readable='N')) -}}
{{ label.add(zebra_zpl_text(hu.handling_unit, position=(40, 320), width=(4*203-80), font_size=40, justification="C")) }}
{{ label.add(zebra_zpl_text(frappe.utils.cstr(hu.qty) + " " + hu.uom, position=(40, 400), width=(4*203-80), font_size=40)) }}
{{ label.add(zebra_zpl_text(hu.item_code, position=(40, 480), width=(4*203-80), font_size=40)) }}
{{ label.add(zebra_zpl_text(hu.warehouse, position=(40, 560), width=(4*203-80), font_size=40)) }}
{{ label.add(zebra_zpl_text(frappe.utils.format_datetime(hu.posting_datetime), position=(40, 640), width=(4*203-80), font_size=40)) }}

{{ label.dump_contents() }}
