html:
{% set hu = get_handling_unit(doc.name) %}

<meta name="pdfkit-orientation" content="Portrait"/>
<div class="barcode-label-container">
    <div id="margin-top">0mm</div>
    <div id="margin-left">0mm</div>
    <div id="margin-bottom">0mm</div>
    <div id="margin-right">0mm</div>

    <div id="barcode-label" class="align-middle">
        <div class="text-center">
        {{barcode128(hu.handling_unit)}}
        </div>
        <div class="text-center item_code">
            <span>{{ hu.handling_unit }}</span><br>
            <span class="ellipsis">{{ frappe.utils.format_datetime(hu.posting_datetime) }}</span>
            <span class="ellipsis">{{ hu.actual_qty }}</span>
            <span class="ellipsis">{{ hu.uom }}</span>
            <span class="ellipsis">{{ hu.item_name }}</span>

        </div>
    </div>
</div>

<style>
#margin-top,#margin-left,#margin-bottom,#margin-right {
    display: none;
}


@media screen, print {
  .barcode-label-container {
    border: 1px solid black;
    padding: 0;
    margin: 0;
    min-width: 3in;
    max-width: 3in;
    min-height: 1in;
    max-height: 1in;
    font-family: Helvetica, sans-serif;
    display: block;
  }
  .text-center {
    margin: 0;
    padding: 0;
  }
  .barcode {
    max-width: 2.8in;
    min-width: 2.8in;
    min-height: .5in;
    max-height: .5in;
  }
  .item_code {
  }
}

</style>
